# =========== SETTINGS BELOW ============

#              Feel free to modify everything! 
#      you'll find all the api settings and configs
#                                     below.

# ====== DEVICE ID GENERATOR LOGIN =======
# [ i ] : Insert an unverified amino account below to activate the built-in deviceId generator.
email = ""
password = ""

# ============ EXCEPTIONS V2 ============
# [ i ] : Choose here if you wanna use HCL.py's exceptions v2 or Amino.py's exceptions.
exceptV2 = True

# [ i ] : Enable or Disable Exceptions v2 Timer
eV2Timer = False

# [ i ] : Change the Exceptions v2 Timer Duration
eV2TimerDuration = 15

# ======== AUTOMATIC WEBSOCKET  ========
# [ i ] : Enable or Disable socket starting at api login and closing at api logout
socketAuto = False

# [ i ] : Choose if socket auto start must be web socket or app socket
sAutoAsWeb = False

# ========= ENFORCE DEBUG MODE ==========
# [ i ] : Force Enable or Disable HCL.py's Debug Mode
debugMode = False

language = "en-US"
