#if you need a help
def help():
  description = """ 
There are few functions here:

1)country_short_name_from_code(code)
ex. country_short_name_from_code(840)
you'll see 'us'.

2)country_code_from_short_name(name)
ex. country_code_from_short_name('US')
you'll see 840. 

3)country_long_name_from_code(code)
ex. country_long_name_from_code(840)
you'll see 'united states'.

4)country_code_from_long_name(name)
ex. country_code_from_long_name('united states')
you'll see 840.


5)country_long_name_from_letters(letters)
ex. country_long_name_from_letters('us')
you'll see 'united states'.

6)country_letters_from_long_name(l_name)
ex. country_letters_from_long_name('united states')
you'll see 'us'.
  """
  return print(description)

#return name
def country_short_name_from_code(code):
  return list(country_list.keys())[list(country_list.values()).index(code)]


#return code
def country_code_from_short_name(name):
  return list(country_list.values())[list(country_list.keys()).index(name.lower())]

#return name
def country_long_name_from_code(code):
  return list(country_codes.keys())[list(country_codes.values()).index(code)]


#return code
def country_code_from_long_name(name):
  return list(country_codes.values())[list(country_codes.keys()).index(name.lower())]

#return long name
def country_long_name_from_letters(letters):
  return list(country_names.keys())[list(country_names.values()).index(letters.lower())]

#return short name
def country_letters_from_long_name(l_name):
  return list(country_names.values())[list(country_names.keys()).index(l_name.lower())]